INSERT INTO `%TABLE_PREFIX%osfaq_settings_lang` (`settings_key`, `language`, `title`, `description`, `last_modified`) VALUES
('OSFDB_DEFAULT_LANG', 'spanish', 'Idioma Predeternimado?', 'El lenguaje utilizado para las etiquetas de preguntas mas frecuentes y los encabezados.<br />Para agregar mas idiomas en el sistema, haga una copia de la carpeta /faq/includes/language/english, cambiale el nombre por el nombre del idioma deseado e inserta el texto traducido en los archivos', '2010-12-07 01:07:32'),
('OSFDB_TIMEZONE', 'spanish', 'Zona horaria Predeterminada?', 'La zona horaria predeterminada de su sitio web. Esto se utiliza para las marcas de tiempo y fechas. Ver: <a href="http://php.net/manual/en/timezones.php" target="_blank">http://php.net/manual/en/timezones.php</a> para obtener mas detalles.', '2010-12-07 01:07:32'),
('OSFDB_ENABLE_SSL', 'spanish', 'Habilitar SSL?', 'true OR false. Activa o desactiva el modo seguro.<br /><b style="color:red">Debe tener SSL instalado y funcionando en el servidor para que esta opcion funcione.</b>', '2010-12-07 01:07:32'),
('OSFDB_DEFAULT_IPP', 'spanish', 'Valor minimo Paginacion?', 'El importe minimo de preguntas frecuentes por pagina antes de que los controles de paginacion se muestren. Utilice un numero entero. Los valores decimales no se tendra en cuenta.', '2010-12-07 01:07:32'),
('OSF_ADMIN_HEADING', 'spanish', 'Configuracion de Administrador', '', '2010-12-07 01:07:32'),
('OSFDB_INACTIVE_COLOR', 'spanish', 'Color de fila utilizado para elementos inactivos del administrador?', 'Color de fila utilizado para elementos inactivos del administrador como un valor de caracter hexagonal de 3 o 6 (mas #). Debe comenzar con # ejemplo: #00FF00 or #0F0', '2010-12-07 01:07:32'),
('OSFDB_ACTIVE_COLOR', 'spanish', 'Color de fila utilizado para elementos activos del administrador?', 'Color de fila utilizado para elementos activos del administrador como un valor de caracter hexagonal de 3 o 6 (mas #). Debe comenzar con # ejemplo: #00FF00 or #0F0', '2010-12-07 01:07:32'),
('OSFDB_STATUS_DEFAULT', 'spanish', 'Nuevo FAQ estado por defecto', 'Ajuste por defecto de estado de FAQs nuevos creados en el admin.<br /><b>true</b> == Activo', '2013-03-29 15:07:00'),
('OSFDB_FEATURE_DEFAULT', 'spanish', 'Nueva defecto FAQ destacado', 'Por defecto featured ajuste para FAQs nuevos creados en el admin.<br /><b>true</b> == Activo', '2013-03-29 15:07:00'),
('OSFDB_WYS_STAFF_THEME', 'spanish', 'WYSIWYG tema', 'WYSIWYG html editor de temas para los funcionarios', '2013-03-03 19:39:30'),
('OSFDB_WYSIWYG_STAFF', 'spanish', 'Habilitar el editor html del personal?', 'Activar el editor html WYSIWYG del personal', '2010-12-07 01:07:32'),
('OSF_CLIENT_HEADING', 'spanish', 'Configuracion del lado del Cliente', '', '2010-12-07 01:07:32'),
('OSFDB_CLIENT_PG_STRIP', 'spanish', 'Controles de Paginacion del lado del cliente', 'Donde se debe mostrar la tira de la paginacion:<br />Utilice un valor numerico entero en el rango de 1, 2 o 3.<br />ejemplo:<br /><b>1</b> = ARRIBA<br /><b>2</b> = ABAJO<br /><b>3</b> = AMBOS<br /><br />Cualquier otro valor resultara en AMBOS.', '2010-12-07 01:07:32'),
('OSFDB_WYS_CLIENT_THEME', 'spanish', 'WYSIWYG tema', 'WYSIWYG html editor de temas para los clientes', '2013-03-03 19:39:30'),
('OSFDB_WYSIWYG_CLIENT', 'spanish', 'Habilitar el editor html del Cliente?', 'Activar el editor html WYSIWYG para las entradas del cliente', '2010-12-07 01:07:32'),
('OSFDB_USER_SUBMITS_ALLOW', 'spanish', 'Permita a los usuarios enviar preguntas frecuentes para su aprobacion?', 'Activa o desactiva el enlace (y la pagina) de envio de preguntas frecuentes del usuario.', '2010-12-07 01:07:32'),
('OSFDB_OPTIONAL_FOOTER', 'spanish', 'Texto de pie de pagina de preguntas frecuentes?', 'Texto a incluir en la parte inferior de las paginas de preguntas frecuentes. Puede ser texto plano o HTML', '2010-12-07 01:07:32'),
('OSFDB_SEARCH_BG_COLOR', 'spanish', 'Color de resaltado de la busqueda de los usuarios?', 'color de resaltado de la busqueda como un valor de caracter hexagonal de 3 o 6 (mas #). Debe comenzar con # ejemplo: #00FF00 or #0F0', '2010-12-07 01:07:32'),
('OSF_SEO_HEADING', 'spanish', 'Configuracion SEO del lado del cliente', '', '2010-12-07 01:07:32'),
('OSFDB_URL_FRIENDLY', 'spanish', 'Activar las URL de SEO en las paginas del cliente?', 'Habilitar URLs amigables para los motores de busqueda de las paginas del cliente<br />Requiere una modificacion al archivo .htaccess para que funcione correctamente.<br />Haga clic en el boton de edicion para mas informacion.', '2010-12-07 01:07:32'),
('OSFDB_MAX_URL_LENGTH', 'spanish', 'Longitud maxima de direccion URL de SEO?', 'La longitud maxima de caracteres de la porcion de la pagina para la URL generada de SEO', '2010-12-07 01:07:32'),
('OSFDB_SEO_REMOVE_JOINERS', 'spanish', 'Retire preposiciones Inglesas', 'Retire preposiciones Inglesas de las URL de SEO. ejemplo: a, and, at, is, or, the, it, of, ...etc', '2010-12-07 01:07:32'),
('OSF_EXT_HEADING', 'spanish', 'Modulo de Preguntas Frecuentes  para paginas de osTicket', '', '2010-12-07 01:07:32'),
('OSFDB_EXT_FAQS_ALLOW', 'spanish', 'Preguntas Frecuentes Externas habilitada?', 'Si es (true), los modulos externo  de preguntas frecuentes mostrara preguntas frecuentes en pantalla, de lo contrario no se visualizara.', '2010-12-07 01:07:32'),
('OSFDB_EXT_LIMIT', 'spanish', 'Limite de preguntas frecuentes externo?', 'El numero maximo de preguntas frecuentes que se muestran por opcion de visualizacion externa.', '2010-12-07 01:07:32'),
('OSFDB_MAX_TXT_LENGTH', 'spanish', 'Longitud maxima del texto de la URL?', 'La longitud maxima de caracteres de la porcion de texto visible de la URL generada que aparecen en el modulo externo de Preguntas frecuentes', '2010-12-07 01:07:32'),
('OSFDB_EXT_FEATURED', 'spanish', 'Mostrar elementos externos destacados?', 'Si es (true), Preguntas frecuentes destacadas se mostrara en los modulos externos de Preguntas Frecuentes.', '2010-12-07 01:07:32'),
('OSFDB_EXT_POPULAR', 'spanish', 'Mostrar Articulos populares externos?', 'Si es (true), los mas populares (por vistas) preguntas mas frecuentes se muestran en modulos externos.', '2010-12-07 01:07:32'),
('OSFDB_EXT_NEW', 'spanish', 'Mostrar Nuevos temas externos?', 'Si es (true), las ultimas preguntas mas frecuentes se muestran en modulos externos .', '2010-12-07 01:07:32'),
('OSFDB_USER_ANON', 'spanish', 'Permitir a usuarios anonimos surgerir nuevas preguntas frecuentes', 'Si es (true), usuarios anonimos podran surgerir nuevas preguntas frecuentes para su aprobacion. Si es (false) solo los usuarios que estan conectados pueden enviar nuevas preguntas frecuentes.', '2010-12-07 01:07:32'),
('OSFDB_STAFF_AS_ADMIN', 'spanish', 'Permite a los miembros del personal tener acceso al sistema de Preguntas Frecuentes?', 'Si es (true), Los miembros del personal podran administrar preguntas frecuentes. Si es (false), solo los administradores pueden administrar Preguntas frecuentes.', '2010-12-07 01:07:32'),
('OSFDB_DISABLE_CLIENT', 'spanish', 'Deshabilitar el lado del cliente?', 'Si es <b>true</b> el lado del cliente no sera accesible.', '2010-12-07 01:07:32'),
('OSF_FEED_HEADING', 'spanish', 'Sindicacion Atom / RSS Feed', '', '2010-12-07 01:07:32'),
('OSFDB_FEED_ALLOW', 'spanish', 'Habilitar sistema Atom / RSS Feed?', 'Si esta habilitado, los visitantes de tu sitio web sera capaz de acceder al feed de distribucion de preguntas frecuentes. Es una buena idea habilitarlo.', '2010-12-07 01:07:32'),
('OSFDB_FEED_ATOM', 'spanish', 'Salida de Documentos Atom 1.0 Feed?', 'Si es (true), el Sistema Feed dara salida a documentos Atom v1.0 (recomendado).<br />De lo contrario el sistema Feed dara salida a documentos RSS v2.0.', '2010-12-07 01:07:32'),
('OSFDB_FEED_CACH', 'spanish', 'Active la cache para el Feed?', 'Si se activa, las preguntas frecuentes se almacenan en cache en un archivo estatico para mejorar el rendimiento. Que se actualizara si el limite de la cache ha caducado.', '2010-12-07 01:07:32'),
('OSFDB_FEED_CACHE_LIMIT', 'spanish', 'Cuanto tiempo (en segundos) antes de actualizar la memoria cache del Feed?', 'El tiempo en segundos antes de que el cache del Feed sea actualizado.<br />EJEMPLOS:<br />1 hora = 3600 segundos<br />1 dia = 86.400 segundos<br />1 semana = 604,800 segundos<br />1 mes = 2419200 segundos', '2010-12-07 01:07:32'),
('OSFDB_FEED_LIMIT', 'spanish', 'Elementos maximos a mostrar en un Feed?', 'Los elementos maximos para incluir al Feed. Se recomienda mantener esta cifra por debajo de 50. 15 es el valor por defecto.', '2010-12-07 01:07:32'),
('OSFDB_FEED_CATEGORIES', 'spanish', 'Mostrar seccion Categorias en la pagina web del Feed?', 'Si es (true), Categorias de preguntas mas frecuentes seran incluidos en el Feed.<br /><br /><b>NOTA:</b> Preguntas mas frecuentes se incluyen por defecto y siempre van a aparecer en la parte superior en los feeds .', '2010-12-07 01:07:32'),
('OSFDB_FEED_FEATURED', 'spanish', 'Filtrar el contenido del Feed por el estado destacados?', 'Si es (true) preguntas mas frecuentes y las categorias (si esta activado) Destacadas seran primero seleccionadas para la salida y aparecera en el inicio del Feed.', '2010-12-07 01:07:32'),
('OSFDB_FEED_DATE', 'spanish', 'Filtrar contenido del Feed por Fecha?', 'Si es (true) La salida del Feed sera filtrada por Fecha. Esta sera la fecha de creacion o modificacion (lo que es mas reciente).<br /><br /><b>NOTA: </b>La opcion de destacados anteriormente tiene una prioridad mas alta que la fecha si los dos estan habilitados.', '2010-12-07 01:07:32'),
('OSFDB_FEED_VIEWS', 'spanish', 'Filtrar el contenido del Feed por Popularidad?', 'Si es (true) La salida del Feed sera filtrada por Popularidad, las mas recientes arriba.<br /><br /><b>NOTA: </b>La opcion Fecha y Destacados mas arriba tienen una prioridad mas alta que la popularidad cuando todas las opciones de filtro estan habilitadas.', '2010-12-07 01:07:32'),
('OSFDB_FEED_RANDOM', 'spanish', 'Filtrar el contenido del Feed Aleatoriamente?', 'Si es (true) La salida del Feed sera filtrada aleatoriamente<br /><br /><b>NOTA: </b>La opcion Fecha, Destacados y Popularidad mas arriba tienen una prioridad mas alta que la opcion aleatoria cuando todas las opciones de filtro estan habilitadas.', '2010-12-07 01:07:32'),
('OSFDB_RECAPTCHA_THEME', 'spanish', 'Tema visual usará reCAPTCHA', 'Define que tema visual usará reCAPTCHA', '2012-01-28 00:22:32'),
('OSFDB_RECAPTCHA_TAB_INDEX', 'spanish', 'Índice de solapa', 'Establece un tabindex para el recuadro de texto de reCAPTCHA. Si otro elemento del formulario usa un tabindex, esto debería especificarse para que la navegación sea más fácil para el usuario. El índice de solapa debe ser un número entero.', '2012-01-28 00:22:32'),
('OSF_FAQ_SUBMIT_PAGE', 'spanish', 'Envios de Clientes', '', '2012-01-25 19:26:32'),
('OSFDB_SHOW_SINGLE', 'spanish', 'Mostrar cada respuesta en su propia página?', 'Si es true, cuando una respuesta se muestra, será la única pregunta frecuente visualiza. De lo contrario, una lista de preguntas frecuentes se expandirá para mostrar la respuesta seleccionada.', '2012-01-22 20:49:32'),
('OSFDB_SHOW_FAQ_COUNTS', 'spanish', 'Mostrar la cantidad de Preguntas frecuentes al lado de las categorías?', 'Si es true cada pregunta frecuente será contada y las cantidad apareceran junto a los enlaces de categoría.', '2012-01-23 17:40:32'),
('OSFDB_INCLUDE_SUBCATS', 'spanish', 'Incluir Subcategorías en el recuento de la categoria padre de preguntas frecuentes?', 'Si es true, la cantidad mostrada será la suma de las Preguntas frecuentes dentro de la categoría y sus subcategorías. De otra manera solamente las preguntas frecuentes dentro de su categoría serán contadas.', '2012-01-22 20:49:32'),
('OSFDB_UPLOAD_EXTENSIONS', 'spanish', 'Extensiones de documentos permitidas?', 'Lista de extensiones de documentos separadas por una coma.<br /><b>Por defecto:</b><br />pdf, ods, odt, txt, doc, docx, xls, xlsx, tab, csv, xml', '2012-01-22 20:49:32'),
('OSFDB_UPLOAD_SIZE', 'spanish', 'Tamaño Max de subida de Documentos?', 'Ajustes <b>limite_memoria, tamaño_max_articulo o tamaño_max_subida_documento</b> a 0 (cero) se revertirá a los ajustes de php.ini. Los ajustes del "<b>Valor</b>" También limitará el tamaño de subida y debe ser igual o menor que el tamaño de php.ini. Por Defecto:<br />5242880', '2012-01-22 20:49:32'),
('OSF_RECAPTCHA_HEADING', 'spanish', 'Ajustes reCAPTCHA para los envios de clientes', '', '2012-01-23 21:09:32'),
('OSFDB_RECAPTCHA_ENABLE', 'spanish', 'Habilitar reCAPTCHA para envios de Clientes?', 'Si es true, se habilitara reCAPTCHA del lado del cliente a traves de la página de envio de preguntas frecuentes. Vas a nesecitar una llave publica y privada reCAPTCHA que la puedes obtener gratis de  https://www.google.com/recaptcha/admin/create', '2012-01-23 21:09:32'),
('OSFDB_RECAPTCHA_PRIVATE_KEY', 'spanish', 'Llave Privada de reCAPTCHA', 'Introduce la llave privada reCAPTCHA para este sitio o obtén una nueva de <a href="https://www.google.com/recaptcha/admin/create">https://www.google.com/recaptcha/admin/create</a>', '2012-01-23 21:09:32'),
('OSFDB_RECAPTCHA_PUBLIC_KEY', 'spanish', 'Llave Publica de reCAPTCHA', 'Introduce la llave publica reCAPTCHA para este sitio o obtén una nueva de <a href="https://www.google.com/recaptcha/admin/create">https://www.google.com/recaptcha/admin/create</a>', '2012-01-23 21:09:32');
